        sweStudentsList.push(sweStudentsName);
      }
    }
    console.log(`Number of students in SWE: ${sweStudents}. List: ${sweStudentsList.join(', ')}`);
  } catch (error) {
    throw new Error('Cannot load the database');
  }
}
module.exports = countStudents;
